import './App.css';
import { useState, useEffect } from 'react'

function DrawBtn(){
	const[btnColor, setBtnColor] = useState(localStorage.getItem("savedBtnColor"));
	useEffect(() => { localStorage.setItem("savedBtnColor", btnColor);}, [btnColor]);
	return(
		<div>
		<button
			className = {btnColor} 
			onClick = {() => setBtnColor((btnColor) => (btnColor === "baseColor" ? "changeColor" : "baseColor"))}
		></button>
		</div>
	);
}

export default DrawBtn
