import { useState, useEffect } from 'react'

function DrawBtn() {
  const [btnColor, setBtnColor] = useState("blue")

  const click = color => {
    setColor(color)
  }

  useEffect(() => {
    document.body.style.backgroundColor = color
  }, [color])

  return (<div>
    <button onClick = {
      () => click("yellow")
    }>Change BG Color</button>
  </div>)
}

export default DrawBtn;
