import { useState, useEffect } from 'react'

function DrawBtn(){
	const[btnColor, setBtnColor] = useState("baseColor");
	return(
		<div className="centeredContainer">
		<style>{`
			.baseColor{background-color: #000000; padding: 1%}
			.changeColor{background-color: #FFF; padding: 1%}
		`}</style>
		<button
			className = {btnColor} 
			onClick = {() => setBtnColor((btnColor) => (btnColor === "baseColor" ? "changeColor" : "baseColor"))}
		>
		</button>
		</div>
	);
}

export default DrawBtn
