import { useState, useEffect } from 'react'

function drawBtn() {

  const [color, setColor] = useState("blue")

  const click = color => {
    setColor(color)
  }

  useEffect(() => {
    document.body.style.backgroundColor = color
  }, [color])

  return (<div>
    <button onClick = {
      () => click("yellow")
    }>Change BG Color</button>
  </div>)
}

export default drawBtn;
